postman_collection_scripts.login_test_script:
if (pm.response.code === 200) {
    const response = pm.response.json();
    pm.environment.set('jwt_token', response.token);
    pm.environment.set('user_id', response.user.id);
    console.log('✅ Token saved:', response.token);
}

postman_collection_scripts.register_test_script:
if (pm.response.code === 201) {
    const response = pm.response.json();
    pm.environment.set('jwt_token', response.token);
    pm.environment.set('user_id', response.user._id);
    console.log('✅ User registered and token saved');
}

postman_collection_scripts.create_course_test_script:
if (pm.response.code === 201) {
    const response = pm.response.json();
    pm.environment.set('test_course_id', response._id);
    console.log('✅ Course created with ID:', response._id);
}
